let images=[];function handleData(e){let a=document.querySelector(".gallery");e.forEach(e=>{let t=document.createElement("img");t.src=`./assets/images/gallery/${e.image}`,t.alt=e.title,t.setAttribute("data-category-id",e.categoryId),t.addEventListener("click",()=>{let a=document.getElementById("modalGallery"),t=document.getElementById("modalImage");a.style.display="block",images.indexOf(e.image),t.src=`./assets/images/gallery/${e.image}`}),a.appendChild(t)})}fetch("./assets/datas/gallery.json").then(e=>e.json()).then(e=>{images=e.gallery.map(e=>e.image),handleData(e.gallery)}).catch(e=>console.error("Erreur de chargement du fichier JSON:",e));const arrowLeft=document.getElementById("arrowL"),arrowRight=document.getElementById("arrowR");function showPreviousImage(){let e=document.getElementById("modalImage"),a=images.indexOf(e.src.split("/").pop());0===a&&(a=images.length);let t=a-1;e.src=`./assets/images/gallery/${images[t]}`}function showNextImage(){let e=document.getElementById("modalImage"),a=images.indexOf(e.src.split("/").pop());a===images.length-1&&(a=-1);let t=a+1;e.src=`./assets/images/gallery/${images[t]}`}arrowLeft.addEventListener("click",()=>{showPreviousImage()}),arrowRight.addEventListener("click",()=>{showNextImage()}),window.addEventListener("click",e=>{let a=document.getElementById("modalGallery");e.target===a&&(a.style.display="none")});